import React from "react";
import { useState } from "react";

function Form() {
  const [nameSurname, setnameSurname] = useState("");
  const [number, setNumber] = useState("");
  const [email, setemail] = useState("");
  const [date, setdate] = useState("");
  const [city, setcity] = useState("");
  const [partDate, setpartDate] = useState("");
  const [packet, setpacket] = useState("");
  const [checkBox, setcheckBox] = useState("");
  const [emailData, setEmailData] = useState({
    nameSurname: nameSurname,
    number: number,
    email: email,
    date: date,
    city: city,
    partDate: partDate,
    packet: packet,
  });
  const sendEmail = (e) => {
    setEmailData({
      nameSurname: nameSurname,
      number: number,
      email: email,
      date: date,
      city: city,
      partDate: partDate,
      packet: packet,
    });
    e.preventDefault();

    fetch("http://localhost:3001/send-email", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(emailData),
      credentials: "include",
      mode: "cors",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const onSubmit = async (values, actions) => {
    // butona tıklandıgında calısacak kod
    console.log(values, actions);
    await new Promise((resolve) => {
      setTimeout(resolve, 3000); // 3sn bekletmeye yarar
    });
    actions.resetForm(); // ustteki bittikten sonra form içindekileri sıfırlama işlemi yapar
  };
  const { values, errors, isSubmitting, handleChange, handleSubmit } =
    useFormik({
      initialValues: {
        nameSurname: "",
        phoneNumber: "",
        email: "",
        dateBirty: "",
        dateParty: "",
        restaurant: "",
        packet: "",
      },
      validationSchema: basicSchemas,
      onSubmit,
    });

  return (
    <div>
      <div className=" h-[1152px] bg-partyYellow flex justify-center items-center">
        <div className=" h-auto">
          <form
            action=""
            className=" w-[1056px] h-auto flex flex-col justify-center items-center"
          >
            <h6>Tüm alanların doldurulması zorunludur.</h6>
            <input
              onChange={handleChange}
              value={values.nameSurname}
              id="nameSurname"
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="text"
              placeholder="Adınız Soyadınız"
            />
            <input
              onChange={handleChange}
              id="phoneNumber"
              value={values.phoneNumber}
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="number"
              placeholder="Cep Telefon No"
            />
            <input
              onChange={handleChange}
              id="email"
              value={values.email}
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="email"
              placeholder="E-Posta"
            />
            <input
              onChange={handleChange}
              value={values.dateBirty}
              id="dateBirty"
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="date"
              placeholder="Doğum Tarihi"
            />
            <select
              className="px-8 py-4 w-[550px] h-14 my-3"
              name=""
              id="restaurant"
              value={values.restaurant}
              onChange={handleChange}
            >
              <option value="">Restoran seçiniz</option>
              <option value="adana">adana</option>
              <option value="mersin">mersin</option>
            </select>
            <input
              onChange={handleChange}
              value={values.dateParty}
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="date"
              name=""
              id="dateParty"
            />
            <select
              className=" px-8 py-4 w-[550px] h-14 my-3"
              name=""
              id="packet"
              value={values.packet}
              onChange={handleChange}
            >
              <option value="">Parti Paketi</option>
              <option value="standart">Standart Paket</option>
              <option value="delüks">Delüks Paket</option>
            </select>

            <input
              onChange={(e) => {
                setcheckBox(e.target.checked);
              }}
              className=" px-8 py-4 w-[550px] h-14 my-3"
              type="checkbox"
              name=""
              id=""
            />

            <button className=" w-[330px] h-12 bg-white text-navbarYellow rounded-full text-xl font-semibold">
              Rezervasyon Yaptır
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Form;
