import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import left from "../img/left.png";
import right from "../img/right.png";
function HomeImg() {
  const [imgMc, setimgMc] = useState([]);
  const [imgNumber, setimgNumber] = useState(0);
  const fetchCouses = async () => {
    try {
      const response = await axios.get("http://localhost:3004/homeImg");
      setimgMc(response.data);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchCouses();
  }, []);

  console.log(imgMc);
  const leftClick = () => {
    if (imgNumber > imgMc.length) {
      setimgNumber(0);
    }
    setimgNumber(imgNumber + 1);
    console.log(imgNumber);
  };
  return (
    <div>
      <div className=" relative w-full h-[675px] bg-gray-400">
        <div>
          <img src={imgMc[imgNumber].img} alt="" />
        </div>
        <div className=" absolute top-0 w-full h-[675px] flex">
          <div className=" w-12 flex items-center" onClick={leftClick}>
            <img src={left} alt="" />
          </div>
          <div className=" w-full  h-full flex flex-col ">
            <div className=" w-full h-full"></div>
            <div className=" w-full h-10 flex justify-center">
              <div className=" border-8 rounded-full w-8 h-8 mx-1"></div>
              <div className=" border-8 rounded-full w-8 h-8 mx-1"></div>
              <div className=" border-8 rounded-full w-8 h-8 mx-1"></div>
              <div className=" border-8 rounded-full w-8 h-8 mx-1"></div>
            </div>
          </div>
          <div className=" w-12 flex items-center">
            <img src={right} alt="" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomeImg;
