import * as yup from "yup";
const passwordRules = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{5,}$/;
export const basicSchemas = yup.object().shape({
  email: yup.string().email("Lütfen geçerli bir email giriniz.").required("Email alanını boş bırakamazsınız."),
  age: yup.number().integer("Lütfen yaşınızı tam sayı olarak giriniz.").positive("Lütfen pozitif bir sayı giriniz.").required("Yaş alanını boş bırakamazsınız."),
  password: yup.string().required("Şifre alanını boş bırakamazsınız.").min(5, "Minimum 5 karakter giriniz.").matches(passwordRules, {
    message: "Lütfen en az 1 büyük harf, 1 küçük harf ve 1 sayı içeren 5 karakterden uzun bir şifre giriniz."
  }),
  confirmPassword: yup.string().oneOf([yup.ref("password")], "Şifreniz eşleşmiyor.").required("Şifre alanını boş bırakamazsınız.")
});
export const advancedSchemas = yup.object().shape({
  kullaniciadi: yup.string().min(3, "Minimum 3 karakterli olmalıdır").required("Kullanıcı adı alanını boş bırakamazsınız."),
  univercity: yup.string().oneOf(["atü", "gaün"], "Üniversitenizi seçiniz.").required("Üniversite alanı boş bırakamazsınız."),
  isAccepted: yup.boolean().oneOf([true], "Kullanım koşullanırını kabul ediniz")
});
