import React from "react";
import { useState } from "react";

function Form() {
  const [nameSurname, setnameSurname] = useState("");
  const [number, setNumber] = useState("");
  const [email, setemail] = useState("");
  const [date, setdate] = useState("");
  const [city, setcity] = useState("");
  const [partDate, setpartDate] = useState("");
  const [packet, setpacket] = useState("");
  const [checkBox, setcheckBox] = useState("");
  const [emailData, setEmailData] = useState({
    nameSurname: nameSurname,
    number: number,
    email: email,
    date: date,
    city: city,
    partDate: partDate,
    packet: packet,
  });
  const sendEmail = (e) => {
    setEmailData({
      nameSurname: nameSurname,
      number: number,
      email: email,
      date: date,
      city: city,
      partDate: partDate,
      packet: packet,
    });
    e.preventDefault();

    fetch("http://localhost:3001/send-email", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(emailData),
      credentials: "include",
      mode: "cors",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };
  return (
    <div>
      <form
        action=""
        className=" w-[1056px] h-auto flex flex-col justify-center items-center"
      >
        <h6>Tüm alanların doldurulması zorunludur.</h6>
        <input
          onChange={(e) => {
            setnameSurname(e.target.value);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="text"
          placeholder="Adınız Soyadınız"
        />
        <input
          onChange={(e) => {
            setNumber(e.target.value);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="number"
          placeholder="Cep Telefon No"
        />
        <input
          onChange={(e) => {
            setemail(e.target.value);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="email"
          placeholder="E-Posta"
        />
        <input
          onChange={(e) => {
            setdate(e.target.value);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="date"
          placeholder="Doğum Tarihi"
        />
        <select
          className="px-8 py-4 w-[550px] h-14 my-3"
          name=""
          id=""
          onChange={(e) => {
            setcity(e.target.value);
          }}
        >
          <option value="">Restoran seçiniz</option>
          <option value="adana">adana</option>
          <option value="mersin">mersin</option>
        </select>
        <input
          onChange={(e) => {
            setpartDate(e.target.value);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="date"
          name=""
          id=""
        />
        <select
          className=" px-8 py-4 w-[550px] h-14 my-3"
          name=""
          id=""
          onChange={(e) => {
            setpacket(e.target.value);
          }}
        >
          <option value="">Parti Paketi</option>
          <option value="standart">Standart Paket</option>
          <option value="delüks">Delüks Paket</option>
        </select>

        <input
          onChange={(e) => {
            setcheckBox(e.target.checked);
          }}
          className=" px-8 py-4 w-[550px] h-14 my-3"
          type="checkbox"
          name=""
          id=""
        />

        <button
          onClick={sendEmail}
          className=" w-[330px] h-12 bg-white text-navbarYellow rounded-full text-xl font-semibold"
        >
          Rezervasyon Yaptır
        </button>
      </form>
    </div>
  );
}

export default Form;
